INSERT INTO `zt_history` VALUES ('1620', '4046', 'spec', '移民管家列表', '<p>移民管家列表</p>\n<p>查询条件：管家姓名、发布状态，</p>\n<p>操作项：查询、新增、置顶、上移、下移、编辑、删除</p>\n<p>展示项：姓名、英文名、头衔、手机号、当前点赞数、综合评分、聘用次数（正在聘用的次数）,所在地区（分公司城市）,发布状态（未发布、已发布）、操作</p>\n<p>点击姓名、点赞、评分、聘用跳转到管家详情对应的页面，点击编辑跳转到编辑页面</p>\n<p>排序按更新时间倒序，最新修改的优先展示</p>\n<p>点击删除提示用户：删除后将数据将重置，确认是否移除列表？确认移除管家列表，取消返回列表页</p>\n<p><img src=\"data/upload/1/201702/0617044008029f5h.png\" alt=\"\" /></p>', '001- <del>移民管家列表</del>\n001+ <ins><p>移民管家列表</p></ins>\n002+ <ins><p>查询条件：管家姓名、发布状态，</p></ins>\n003+ <ins><p>操作项：查询、新增、置顶、上移、下移、编辑、删除</p></ins>\n004+ <ins><p>展示项：姓名、英文名、头衔、手机号、当前点赞数、综合评分、聘用次数（正在聘用的次数）,所在地区（分公司城市）,发布状态（未发布、已发布）、操作</p></ins>\n005+ <ins><p>点击姓名、点赞、评分、聘用跳转到管家详情对应的页面，点击编辑跳转到编辑页面</p></ins>\n006+ <ins><p>排序按更新时间倒序，最新修改的优先展示</p></ins>\n007+ <ins><p>点击删除提示用户：删除后将数据将重置，确认是否移除列表？确认移除管家列表，取消返回列表页</p></ins>\n008+ <ins><p><img src=\"data/upload/1/201702/0617044008029f5h.png\" alt=\"\" /></p></ins>');
