INSERT INTO `zt_storyspec` VALUES ('69', '1', '问卷管理-保存、复制问卷', '<p class=\"MsoListParagraph\" align=\"left\" style=\"margin-left:23.25pt;text-indent:0cm;\">l&nbsp; 保存问卷：新增问卷或复制问卷，第一条问题保存成功后，即可看到保存问卷，点击保存问卷后，，同时问卷状态为未发布，发布时间为空。未发布的问卷只能有一套，可进行编辑、修改权重、排序、新增问题，保存时该版本不变。<span></span></p>\n<p class=\"MsoListParagraph\" align=\"left\" style=\"margin-left:44.25pt;text-indent:-21pt;\">l&nbsp; 复制问卷： 点击复制问卷，提示用户：“是否复制当前版本（版本号）并创建新问卷？”确认后，将当前版本的问卷复制一份，问卷版本在当前最新版本基础上递增一个新版本，状态为未发布。<span></span></p>\n<p class=\"MsoNormal\" align=\"left\" style=\"margin-left:23.25pt;text-indent:21pt;\">未发布的问卷可有多套。可在未发布的问卷上进行排序、编辑、删除、新增问题、保存问卷、发布<span></span></p>', '');
INSERT INTO `zt_storyspec` VALUES ('4', '1', '微网站前台个人中心', '的发送到', '第三方三点');
