INSERT INTO `zt_storyspec` VALUES ('49', '1', '需要统一微信官网的页面风格', '<p>目前移民项目和专属活动等的页面风格作为独立模块使用没有问题，如果由官网统一入口进入，需要保证整体风格统一。</p>\n<p>希望V1.4.0版能够实现。</p>', '所有由微站官网入口进入的页面风格必须是统一的');
INSERT INTO `zt_storyspec` VALUES ('50', '1', '目录树-增加入口', '在网站左侧目录中增加评估管理入口以及其下级目录“问卷管理”', '');
