INSERT INTO `zt_storyspec` VALUES ('53', '1', '问卷-编辑已有问卷', '如果选择了未发布的问卷，显示各问题 ，并可以对问题进行操作<br />\r\n对问题的操作支持置顶、上移、下移，直接在列表页调整<br />\r\n对问题的调整支持编辑，进入问题编辑页<br />\r\n支持删除问题，在删除前需弹窗，由用户确认操作后再进行删除<br />\r\n', '');
INSERT INTO `zt_storyspec` VALUES ('54', '1', '问卷-新增/编辑问题', '点击新增问题按钮，进入问题新增页面<br />\r\n页面中对问题进行编辑，设置题目和答案，以及答案与选项之间的匹配关系', '');
